import winston from 'winston';
import DailyRotateFile from 'winston-daily-rotate-file';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Get environment
const NODE_ENV = process.env.NODE_ENV || 'development';
const isDevelopment = NODE_ENV === 'development';
const isProduction = NODE_ENV === 'production';

// Create logs directory if it doesn't exist
import fs from 'fs';
const logsDir = path.join(__dirname, '..', 'logs');
if (!fs.existsSync(logsDir)) {
  fs.mkdirSync(logsDir, { recursive: true });
}
// Ensure today's hierarchical directory exists (YYYY/MM/DD)
const now = new Date();
const yearDir = path.join(logsDir, String(now.getFullYear()));
const monthDir = path.join(yearDir, String(now.getMonth() + 1).padStart(2, '0'));
const dayDir = path.join(monthDir, String(now.getDate()).toString().padStart(2, '0'));
if (!fs.existsSync(dayDir)) {
  fs.mkdirSync(dayDir, { recursive: true });
}

// Define log levels
const levels = {
  error: 0,
  warn: 1,
  info: 2,
  http: 3,
  debug: 4,
};

// Define colors for each level
const colors = {
  error: 'red',
  warn: 'yellow',
  info: 'green',
  http: 'magenta',
  debug: 'white',
};

// Tell winston that you want to link the colors
winston.addColors(colors);

// Define which level to log based on environment
const level = () => {
  if (isDevelopment) return 'debug';
  if (isProduction) return 'info';
  return 'info';
};

// Define format for logs
const format = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss:ms' }),
  winston.format.colorize({ all: true }),
  winston.format.printf(
    (info) => `${info.timestamp} ${info.level}: ${info.message}`,
  ),
);

// Define format for file logs (without colors)
const fileFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss:ms' }),
  winston.format.errors({ stack: true }),
  winston.format.uncolorize(),
  winston.format.json(),
);

// Define transports
const transports = [
  // Console transport
  new winston.transports.Console({
    format: winston.format.combine(
      winston.format.colorize(),
      winston.format.simple()
    ),
  }),
  
  // Error log file - Hierarchical structure: logs/YYYY/MM/DD/error.log
  new DailyRotateFile({
    dirname: logsDir,
    filename: '%DATE%/error-%DATE%.log',
    datePattern: 'YYYY/MM/DD',
    level: 'error',
    format: fileFormat,
    maxSize: '20m',
    maxFiles: '14d',
    zippedArchive: true,
    createSymlink: true,
    symlinkName: 'error-current.log',
  }),
  
  // Combined log file - Hierarchical structure: logs/YYYY/MM/DD/combined.log
  new DailyRotateFile({
    dirname: logsDir,
    filename: '%DATE%/combined-%DATE%.log',
    datePattern: 'YYYY/MM/DD',
    format: fileFormat,
    maxSize: '20m',
    maxFiles: '14d',
    zippedArchive: true,
    createSymlink: true,
    symlinkName: 'combined-current.log',
  }),

  // API logs - Hierarchical structure: logs/YYYY/MM/DD/api.log
  new DailyRotateFile({
    dirname: logsDir,
    filename: '%DATE%/api-%DATE%.log',
    datePattern: 'YYYY/MM/DD',
    level: 'http',
    format: fileFormat,
    maxSize: '20m',
    maxFiles: '14d',
    zippedArchive: true,
    createSymlink: true,
    symlinkName: 'api-current.log',
  }),

  // Database logs - Hierarchical structure: logs/YYYY/MM/DD/database.log
  new DailyRotateFile({
    dirname: logsDir,
    filename: '%DATE%/database-%DATE%.log',
    datePattern: 'YYYY/MM/DD',
    level: 'info',
    format: fileFormat,
    maxSize: '20m',
    maxFiles: '14d',
    zippedArchive: true,
    createSymlink: true,
    symlinkName: 'database-current.log',
  }),

  // Security logs - Hierarchical structure: logs/YYYY/MM/DD/security.log
  new DailyRotateFile({
    dirname: logsDir,
    filename: '%DATE%/security-%DATE%.log',
    datePattern: 'YYYY/MM/DD',
    level: 'warn',
    format: fileFormat,
    maxSize: '20m',
    maxFiles: '30d', // Keep security logs longer
    zippedArchive: true,
    createSymlink: true,
    symlinkName: 'security-current.log',
  }),
];

// Create the logger
const logger = winston.createLogger({
  level: level(),
  levels,
  format,
  transports,
  exitOnError: false,
});

// Create a stream object for Morgan
logger.stream = {
  write: (message) => {
    logger.http(message.trim());
  },
};

// Add request logging middleware
export const requestLogger = (req, res, next) => {
  const start = Date.now();
  
  res.on('finish', () => {
    const duration = Date.now() - start;
    const logMessage = `${req.method} ${req.originalUrl} ${res.statusCode} ${duration}ms - ${req.ip}`;
    
    if (res.statusCode >= 400) {
      logger.warn(logMessage);
    } else {
      logger.info(logMessage);
    }
  });
  
  next();
};

// Add error logging middleware
export const errorLogger = (err, req, res, next) => {
  logger.error('Error occurred:', {
    error: err.message,
    stack: err.stack,
    url: req.originalUrl,
    method: req.method,
    ip: req.ip,
    userAgent: req.get('User-Agent'),
  });
  next(err);
};

// Utility function to get log file path for a specific date
export const getLogFilePath = (date, logType = 'combined') => {
  const targetDate = new Date(date);
  const year = targetDate.getFullYear();
  const month = (targetDate.getMonth() + 1).toString().padStart(2, '0');
  const day = targetDate.getDate().toString().padStart(2, '0');
  
  return path.join(logsDir, year.toString(), month, day, `${logType}.log`);
};

// Utility function to list available log dates
export const getAvailableLogDates = () => {
  const dates = [];
  const yearDirs = fs.readdirSync(logsDir).filter(dir => /^\d{4}$/.test(dir));
  
  yearDirs.forEach(year => {
    const yearPath = path.join(logsDir, year);
    const monthDirs = fs.readdirSync(yearPath).filter(dir => /^\d{2}$/.test(dir));
    
    monthDirs.forEach(month => {
      const monthPath = path.join(yearPath, month);
      const dayDirs = fs.readdirSync(monthPath).filter(dir => /^\d{2}$/.test(dir));
      
      dayDirs.forEach(day => {
        dates.push(`${year}-${month}-${day}`);
      });
    });
  });
  
  return dates.sort();
};

// Export logger instance
export default logger;
